DELIMITER $$
--
-- Procédures
--
DROP PROCEDURE IF EXISTS `clean_old_articles`$$
CREATE DEFINER=`root`@`localhost` PROCEDURE `clean_old_articles` (IN `days_to_keep` INT)   BEGIN
    DELETE FROM gdelt_articles_scored 
    WHERE day < DATE_SUB(CURDATE(), INTERVAL days_to_keep DAY)
    AND final_score < 60;
    
    SELECT ROW_COUNT() AS deleted_rows;
END$$

DROP PROCEDURE IF EXISTS `get_country_articles`$$
CREATE DEFINER=`root`@`localhost` PROCEDURE `get_country_articles` (IN `country_code` VARCHAR(3), IN `limit_count` INT)   BEGIN
    SELECT 
        global_event_id,
        day,
        article_title,
        final_score,
        llm_score,
        source_url
    FROM gdelt_articles_scored
    WHERE action_geo_country_code = country_code
    ORDER BY final_score DESC, day DESC
    LIMIT limit_count;
END$$

DELIMITER ;

