DROP TABLE IF EXISTS `gdelt_articles_scored`;
CREATE TABLE IF NOT EXISTS `gdelt_articles_scored` (
  `id` int NOT NULL AUTO_INCREMENT,
  `global_event_id` bigint NOT NULL,
  `day` date NOT NULL,
  `month_year` int DEFAULT NULL,
  `year` int DEFAULT NULL,
  `date_added` datetime NOT NULL,
  `actor1_name` varchar(500) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `actor1_country_code` varchar(3) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `actor2_name` varchar(500) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `actor2_country_code` varchar(3) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `event_code` varchar(10) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `event_root_code` varchar(2) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `quad_class` tinyint DEFAULT NULL,
  `goldstein_scale` float DEFAULT NULL,
  `num_mentions` int DEFAULT '0',
  `num_sources` int DEFAULT '0',
  `num_articles` int DEFAULT '0',
  `avg_tone` float DEFAULT NULL,
  `actor1_geo_country_code` varchar(3) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `actor2_geo_country_code` varchar(3) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `action_geo_country_code` varchar(3) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `action_geo_fullname` varchar(500) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `action_geo_lat` float DEFAULT NULL,
  `action_geo_long` float DEFAULT NULL,
  `source_url` text COLLATE utf8mb4_unicode_ci,
  `article_title` text COLLATE utf8mb4_unicode_ci,
  `article_content` longtext COLLATE utf8mb4_unicode_ci,
  `article_language` varchar(5) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `article_author` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `article_publish_date` datetime DEFAULT NULL,
  `llm_score` tinyint DEFAULT NULL COMMENT 'Score LLM de 0 à 4',
  `llm_justification` text COLLATE utf8mb4_unicode_ci,
  `final_score` float DEFAULT NULL COMMENT 'Score final combiné de 0 à 100',
  `is_oil_country` tinyint(1) DEFAULT '0',
  `keyword_matches` text COLLATE utf8mb4_unicode_ci COMMENT 'Mots-clés matchés (JSON)',
  `processed_at` datetime DEFAULT CURRENT_TIMESTAMP,
  `updated_at` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `full_tittle` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `global_event_id` (`global_event_id`),
  KEY `idx_day` (`day`),
  KEY `idx_date_added` (`date_added`),
  KEY `idx_final_score` (`final_score`),
  KEY `idx_action_country` (`action_geo_country_code`),
  KEY `idx_event_code` (`event_code`),
  KEY `idx_actor1_country` (`actor1_country_code`),
  KEY `idx_actor2_country` (`actor2_country_code`),
  KEY `idx_language` (`article_language`),
  KEY `idx_combined_score_date` (`final_score`,`day`)
) ENGINE=InnoDB AUTO_INCREMENT=101 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='Articles GDELT scorés pour prédiction prix pétrole';

